DROP TABLE IF EXISTS `config`;
CREATE TABLE `config` ( `config_id` int(11) NOT NULL AUTO_INCREMENT, `reg_name` varchar(100) DEFAULT NULL, `inst_name` varchar(100) DEFAULT NULL, `act_key` varchar(150) NOT NULL DEFAULT 'f095ee552eba039e3cc63075771ad5e3', `reg_time` date DEFAULT NULL, `trial_limit` int(11) NOT NULL DEFAULT '30', `act_status` varchar(150) NOT NULL DEFAULT '34e8da6f14bcb12583c9e609e07eb855', PRIMARY KEY (`config_id`)) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `division`;
CREATE TABLE `division` ( `division_id` int(11) NOT NULL AUTO_INCREMENT, `division_name` varchar(100) DEFAULT NULL, PRIMARY KEY (`division_id`)) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `grades`;
CREATE TABLE `grades` ( `grade_id` int(11) NOT NULL AUTO_INCREMENT, `grade_name` varchar(45) DEFAULT NULL, `grade_max` int(11) DEFAULT NULL, `grade_top` float DEFAULT NULL, `grade_down` float DEFAULT NULL, PRIMARY KEY (`grade_id`)) ENGINE=InnoDB AUTO_INCREMENT=154 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `marks`;
CREATE TABLE `marks` ( `record_id` int(11) NOT NULL AUTO_INCREMENT, `student_id` int(11) DEFAULT NULL, `role_id` int(11) DEFAULT NULL, `column_id` int(11) DEFAULT NULL, `marks` float DEFAULT NULL, PRIMARY KEY (`record_id`)) ENGINE=InnoDB AUTO_INCREMENT=265 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `report_columns`;
CREATE TABLE `report_columns` ( `column_id` int(11) NOT NULL AUTO_INCREMENT, `column_name` varchar(100) DEFAULT NULL, `column_level` int(11) DEFAULT NULL, `column_parent` int(11) DEFAULT NULL, PRIMARY KEY (`column_id`)) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `report_structure`;
CREATE TABLE `report_structure` ( `structure_id` int(11) NOT NULL AUTO_INCREMENT, `role_id` int(11) DEFAULT NULL, `column_id` int(11) DEFAULT NULL, `maxmarks` int(11) DEFAULT NULL, PRIMARY KEY (`structure_id`)) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `sessions`;
CREATE TABLE `sessions` ( `sessions_id` int(11) NOT NULL AUTO_INCREMENT, `sessions_name` varchar(100) DEFAULT NULL, `sessions_parent` int(11) DEFAULT NULL,  PRIMARY KEY (`sessions_id`)) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `standard`;
CREATE TABLE `standard` ( `standard_id` int(11) NOT NULL, `standard_name` varchar(45) DEFAULT NULL, PRIMARY KEY (`standard_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `standard_syllabus`;
CREATE TABLE `standard_syllabus` ( `syllabus_id` int(11) NOT NULL AUTO_INCREMENT, `standard_id` int(11) DEFAULT NULL, `subject_id` int(11) DEFAULT NULL,  PRIMARY KEY (`syllabus_id`)) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `student`;
CREATE TABLE `student` ( `student_id` int(11) NOT NULL AUTO_INCREMENT, `student_ref` int(11) DEFAULT NULL, `student_name` varchar(50) DEFAULT NULL, `student_roll` int(11) DEFAULT NULL, `standard_id` int(11) DEFAULT NULL, `division_id` int(11) DEFAULT NULL, PRIMARY KEY (`student_id`)) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `subject`;
CREATE TABLE `subject` ( `subject_id` int(11) NOT NULL AUTO_INCREMENT, `subject_name` varchar(100) DEFAULT NULL, PRIMARY KEY (`subject_id`)) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `teacher`;
CREATE TABLE `teacher` ( `teacher_id` int(11) NOT NULL AUTO_INCREMENT, `teacher_name` varchar(100) DEFAULT NULL, `teacher_pass` varchar(200) DEFAULT NULL, `teacher_email` varchar(100) DEFAULT NULL, `ip` varchar(225) DEFAULT NULL, `lastlogin` date DEFAULT NULL, `activated` int(11) DEFAULT '0', `session_id` int(11) DEFAULT NULL, PRIMARY KEY (`teacher_id`)) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS `teacher_roles`;
CREATE TABLE `teacher_roles` ( `role_id` int(11) NOT NULL AUTO_INCREMENT, `teacher_id` int(11) DEFAULT NULL, `standard_id` int(11) DEFAULT NULL, `division_id` int(11) NOT NULL DEFAULT '1', `subject_id` int(11) DEFAULT NULL, `structure_lock` int(11) NOT NULL DEFAULT '0', `marks_lock` int(11) NOT NULL DEFAULT '0', PRIMARY KEY (`role_id`)) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;
